$(document).ready(function(){$(".category-link").on("click",function(){let r=new URL(window.location.href);r.searchParams.set("category",$(this).data("id"));var o=r.href;$(location).prop("href",o)}),$(".brand-link").on("click",function(){let r=new URL(window.location.href);r.searchParams.set("marque",$(this).data("id"));var o=r.href;$(location).prop("href",o)}),$(".vendor-link").on("click",function(){let r=new URL(window.location.href);r.searchParams.set("vendor",$(this).data("id"));var o=r.href;$(location).prop("href",o)}),$("#sort-prods").on("change",function(){let r=new URL(window.location.href);r.searchParams.set("trier",$(this).val());var o=r.href;$(location).prop("href",o)})});
